import { createSlice } from "@reduxjs/toolkit";

initialCart = { showCart: false };
const cartSlice = createSlice({
  name: "cart",
  initialState: initialCart,
  reducers: {
    toggleCart() {
      showCart = !showCart;
    },
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
